import { Component } from '@angular/core';
import { CalcLine } from '../calc-line';
import { CALC_LINES } from '../mock-calcs';

@Component({
  selector: 'app-calcs-list',
  templateUrl: './calcs-list.component.html',
  styleUrls: ['./calcs-list.component.css']
})

export class CalcsListComponent {
	selectedLine? : CalcLine;
	onSelect(line: CalcLine): void {
		this.selectedLine = line;
	}
	lines = CALC_LINES;

}
<h2>My Calcs</h2>
<ul class="lines">
  <li *ngFor="let each of lines" >
    <button [class.selected]="each === selectedLine" type="button" (click)="onSelect(each)">
      <span class="badge">{{each.id}}</span>
      <span class="name">{{each.name}}</span>
    </button>
  </li>
</ul>
<app-calc-line [line]="selectedLine"></app-calc-line>
