import { Component } from '@angular/core';
import { CalcItem } from '../calc-item';

@Component({
  selector: 'app-calc-line',
  templateUrl: './calc-line.component.html',
  styleUrls: ['./calc-line.component.css'],

})


export class CalcLineComponent {
	item : CalcItem = {
		id: 1,
		name: 'Placeholder for name',
	};
}
<h2>{{item.name | uppercase}} Details</h2>
<div>
  <label for="name">Item name: </label>
  <input id="name" [(ngModel)]="item.name" placeholder="name">
</div>
