import { Component } from '@angular/core';
import { CalcLine } from '../calc-line';
import { CALC_LINES } from '../mock-calcs';

@Component({
  selector: 'app-calc-line',
  templateUrl: './calc-line.component.html',
  styleUrls: ['./calc-line.component.css'],

})


export class CalcLineComponent {
	selectedLine? : CalcLine;
	onSelect(line: CalcLine): void {
		this.selectedLine = line;
	}
	lines = CALC_LINES;
}
<!-- h2>{{item.name | uppercase}} Details</h2>
<div>
  <label for="name">Item name: </label>
  <input id="name" [(ngModel)]="item.name" placeholder="name">
</div -->
<h2>My Calcs</h2>
<ul class="lines">
  <li *ngFor="let each of lines" >
    <button type="button" (click)="onSelect(each)">
      <span class="badge">{{each.id}}</span>
      <span class="name">{{each.name}}</span>
    </button>
  </li>
</ul>
<div *ngIf="selectedLine">
  <h2>{{selectedLine.name | uppercase}} Details</h2>
  <div>id: {{selectedLine.id}}</div>
  <div>
    <label for="line-name">CalcLine name: </label>
    <input id="line-name" [(ngModel)]="selectedLine.name" placeholder="name">
  </div>
</div>
