import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';

import { Observable, of } from 'rxjs';
import { catchError, map, tap } from 'rxjs/operators';

import { CalcLine } from './calc-line';
import { MessageService } from './message.service';

@Injectable({
  providedIn: 'root'
})
export class CalcLineService {
	private linesUrl = 'http://localhost:4201/mock-calcs.json';  // URL to web api

	constructor(
		private http: HttpClient,
		private messageService: MessageService
	) { }

	private log(message: string) {
		this.messageService.add(`CalcLineService: ${message}`);
	}

	getAll(): Observable<CalcLine[]> {
		const result = this.http.get<CalcLine[]>(this.linesUrl)
			.pipe(
				catchError(this.handleError<CalcLine[]>('getAll', []))
			);
		this.log('fetched all');
		return result;
	}

	getLine(id: number): Observable<CalcLine> {
		const line = CALC_LINES.find(each => each.id === id)!;
		this.log(`fetched line id=${id}`);
		return of(result);
	}

	goBack(): void {
		this.location.back();
	}
}
