import { Injectable } from '@angular/core';
import { CalcLine } from './calc-line';
import { CALC_LINES } from './mock-calcs';
import { Observable, of } from 'rxjs';
import { MessageService } from './message.service';

@Injectable({
  providedIn: 'root'
})
export class CalcLineService {

	constructor(private messageService: MessageService) { }

	getAll(): Observable<CalcLine[]> {
		const lines = of(CALC_LINES);
		this.messageService.add('CalcLineService: fetched all');
		return lines;
	}

	getLine(id: number): Observable<CalcLine> {
		const result = CALC_LINES.find(each => each.id === id)!;
		this.messageService.add(`CalcLineService: fetched line id=${id}`);
		return of(result);
	}
}
