</div -->
<h2>My Heroes</h2>
<ul class="calcs">
  <li *ngFor="let item of calcs" >
    <button type="button" (click)="onSelect(hero)">
      <span class="badge">{{item.id}}</span>
      <span class="name">{{item.name}}</span>
    </button>
  </li>
</ul>
